package com.javaexm.me;

public class Multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int a = 1; a <= 10; a++) {
			for (int b = 1; b <= 10; b++) {
				int sum = a * b;
				System.out.println(a + "X" + b + "=" + sum);
			}
			System.out.println(" ");
		}

	}

}
